import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from "react-router-dom";
import { useState, useEffect } from "react";
import CustomerTable from './TableData/CustomerTableData';
import axios from "axios";

//const gitHubUrl = "https://api.github.com/users/isaac930";
const gitHubUrl = "https://api.github.com/users/deekshasharma";
 
const Customer = () => {
  let history = useHistory();

  function handleClick() {
    history.push("/");
  }

  const [userData, setUserData] = useState({});

  useEffect(() => {
    getGiHubUserWithAxios();
  }, []);

  const getGiHubUserWithAxios = async () => {
    const response = await axios.get(gitHubUrl);
    setUserData(response.data);
  };

  return (
<div>

   <div>
 <div class="card">
  <div class="card-header">
  
  <button type="button" class="btn btn-white btn-circle btn-xl nav-link d-flex justify-content-end" onClick={handleClick}>
  Navigate
  </button>
  <marquee><h1 style={{color:'red;'}}>This is service Genie Admin Templated Revised In React Js From Html Version
    </h1>
  </marquee>
  </div>
  <div class="card-body">
    <div>
    <table>
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Region</th>
          <th>Memory</th>
          <th>CPUs</th>
          <th>Disk Size</th>
        </tr>
      </thead>
      <tbody>
        <tr>
    
        <td>{userData.name}</td>
        <td>{userData.location}</td>
        <td>{userData.blog}</td>
        <td>{userData.company}</td>
        </tr>
      </tbody>
    </table>
    
      </div>
</div>
  </div>
  <div class="card-footer"></div>
</div>
     </div>


  );


};
  
export default Customer;
