import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from "react-router-dom";
import { useState, useEffect } from "react";
import CustomerTable from './TableData/CustomerTableData';
import axios from "axios";

const gitHubUrl = "https://api.github.com/users/deekshasharma";
 
const Customer = () => {
  let history = useHistory();

  function handleClick() {
    history.push("/");
  }

  const [userData, setUserData] = useState({});

  useEffect(() => {
    getGiHubUserWithAxios();
  }, []);

  const getGiHubUserWithAxios = async () => {
    const response = await axios.get(gitHubUrl);
    setUserData(response.data);
  };

  return (
<div>

   <div>
 <div class="card">
  <div class="card-header">
  
  <button type="button" class="btn btn-white btn-circle btn-xl nav-link d-flex justify-content-end" onClick={handleClick}>
  Navigate
  </button>
  <marquee><h1 style={{color:'red;'}}>This is service Genie Admin Templated Revised In React Js From Html Version
    </h1>
  </marquee>
  </div>
  <div class="card-body">
    <div>
    <header className="App-header">
        <h2>GitHub User Data</h2>
      </header>
      <div className="user-container">
        <h5 className="info-item">{userData.name}</h5>
        <h5 className="info-item">{userData.location}</h5>
        <h5 className="info-item">{userData.blog}</h5>
        <h5 className="info-item">{userData.company}</h5>
      </div>
</div>
  </div>
  <div class="card-footer"></div>
</div>
     </div>


     </div>
  );


};
  
export default Customer;
